<script>
	import {onMount} from 'svelte'
	import { debounce } from "debounce";
	import md5 from 'blueimp-md5'
	import {MD5_KEY} from "./puzzle-reader-constant";
	import {domain} from "./puzzle-reader-constant";

	export let file_name
	export let file_page_number
	export let width
	export let height

	let canvas_el
	let element_width
	let protect_mode = false
	let watermark_el

	$: tile_w = width / 5
	$: tile_h = height / 5

	$: {
		if (file_page_number >= 0) render()
	}

	export const getImageFile = () => {
		console.log('get image file blob')
		canvas_el.toBlob((blob) => {
			//console.log('cliff: ', blob)
			console.log(URL.createObjectURL(blob))
		})
	}

	const render = () => {
		for (let i = 0; i < 5 ; i++){
			for (let j = 0; j < 5 ; j++){
				let image = new Image()
				// image.crossOrigin="anonymous"
				image.onload = () => {
					const ctx = canvas_el.getContext('2d')
					let x = i * tile_w
					let y= j * tile_h * 0.995
					ctx.drawImage(image, x, y)
				}
				let filename = md5(`${file_name}_${file_page_number}_${i}_${j}`, MD5_KEY)
				image.src = `${domain}/${filename}.jpg`
			}
		}
	}

	const addWatermark = () => {
		const ctx = canvas_el.getContext('2d')
		ctx.drawImage(watermark_el, 20, 20, tile_w, tile_w)
	}

	onMount(() => {
		element_width = calculateWidth()
		render()
		const resizeHandler = () => element_width = calculateWidth()
		const dHandler = debounce(resizeHandler, 200)
		window.addEventListener('resize', dHandler)
		return () => window.removeEventListener('resize', dHandler)
	})

	const calculateWidth = () => {
		let w1 = window.innerHeight * 0.99 * (width / height)
		let w2 = window.innerWidth * 0.99
		return Math.min(w1, w2)
	}

	const onBlur = () => {
		// TODO: note: allow teacher to leave browser when teacher use the zoom pencil
		// protect_mode = true
		// addWatermark()
	}

	const onFocus = () => {
		// TODO: note: allow teacher to leave browser when teacher use the zoom pencil
		// protect_mode = false
		// render()
	}

	const onKeydown = (e) => {
		if (e.keyCode === 91 && !protect_mode) {
			protect_mode = true
			addWatermark()
		}
	}

	const onKeyup = (e) => {
		if (protect_mode) {
			protect_mode = false
			render()
		}
	}

	const onMousedown = () => {
		if (protect_mode) {
			protect_mode = false
			render()
		}
	}
</script>

<svelte:window on:mousedown={onMousedown} on:keyup={onKeyup} on:keydown={onKeydown} on:blur={onBlur} on:focus={onFocus}/>

<div class="w-full h-full flex items-center justify-center">
	<canvas class="rounded-lg" style="width: {element_width}px; max-width: 100vw; max-height:100vh;" {width} {height} bind:this={canvas_el}></canvas>
</div>

<img class="hidden" bind:this={watermark_el} src="data:image/png;base64," alt="watermark" />
